import "ol/ol.css";
import React, { useState, useRef, useEffect } from "react";
import pinData from "./data/pinData";
import circleRed from "./images/circleRed.png";
import Map from "ol/Map";
import TileLayer from "ol/layer/Tile";
import View from "ol/View";
import OSM from "ol/source/OSM";
import { fromLonLat } from "ol/proj";
import createPinLayer from "./utilities/createPinLayer";

const locationString = "location";

const UserMap = () => {
  const [map, setMap] = useState();
  const [cookie, setCookie] = useState();
  const mapElement = useRef();

  // Return true if the user reveals their location.
  const canGetLocation = () => {
    if (!navigator.geolocation) return false;

    if (sessionStorage.getItem(locationString)) return true;

    navigator.geolocation.getCurrentPosition(
      // On success.
      (position) => {
        let coords = {
          lon: position.longitude,
          lat: position.latitude
        }
        sessionStorage.setItem(locationString, JSON.stringify(coords));
        return true;
      },
      // On fail.
      () => {
        return false;
      }
    );
  };

  const updateLocation = () => {
    let coords = sessionStorage.getItem(locationString);
    console.log(coords);

    map.addLayer(createPinLayer(coords.longitude, coords.latitude, "me", circleRed));
  };

  const addPins = () => {
    Object.keys(pinData).forEach((key) => {
      let name = key;
      let crab = pinData[key];

      map.addLayer(createPinLayer(crab.lon, crab.lat, name, circleRed));
    });
  };

  const createMap = () => {
    const initialMap = new Map({
      target: mapElement.current,
      layers: [
        new TileLayer({
          source: new OSM(),
        }),
      ],
      view: new View({
        center: fromLonLat([37.41, 8.82]),
        zoom: 4,
      }),
    });

    setMap(initialMap);
  };

  useEffect(() => {
    if (!map) {
      createMap();
      return;
    }

    addPins();

    if (canGetLocation()) {
      setInterval(updateLocation(), 5000);
    }
  });

  return <div ref={mapElement} style={{ height: "600px", width: "100%" }}></div>;
};

export default UserMap;
